SELECT 
    l.name AS 'Label',
    COUNT(DISTINCT el.tblEmailSampleId) AS 'Email Count'
FROM tblLabel l
LEFT JOIN tblEmailLabel el ON l.id = el.tblLabelId
LEFT JOIN tblEmailSample e ON el.tblEmailSampleId = e.id
WHERE e.sender = 'dataset@training.com'
GROUP BY l.id, l.name
ORDER BY COUNT(DISTINCT el.tblEmailSampleId) DESC;

-- Summary statistics
SELECT 
    'Total Training Emails' AS 'Metric',
    COUNT(*) AS 'Count'
FROM tblEmailSample
WHERE sender = 'dataset@training.com'
UNION ALL
SELECT 
    'Total Email-Label Relationships' AS 'Metric',
    COUNT(*) AS 'Count'
FROM tblEmailLabel el
JOIN tblEmailSample e ON el.tblEmailSampleId = e.id
WHERE e.sender = 'dataset@training.com'
UNION ALL
SELECT 
    'Dataset Entries' AS 'Metric',
    COUNT(*) AS 'Count'
FROM tblDataset
WHERE name = 'Training Dataset - Multi-label';

-- Label distribution
SELECT 
    COUNT(DISTINCT el.tblEmailSampleId) AS 'Email Count',
    COUNT(el.tblLabelId) AS 'Total Labels'
FROM tblEmailLabel el
JOIN tblEmailSample e ON el.tblEmailSampleId = e.id
WHERE e.sender = 'dataset@training.com';

